mapper.update:
UPDATE POPUP_MNG
SET POPUP_NAME = #{popupName}
	,START_DATE = #{startDate}
	,END_DATE = #{endDate}
	,CONTENT = #{content}
	,FILE_ID = #{fileId}
	,URL = #{url}
	,USE_YN = 'Y'
	,CLOSE_TYPE = #{closeType}
WHERE POPUP_ID = #{popupId}

UPDATE POPUP_MNG
SET USE_YN = 'N'
WHERE POPUP_ID =  #{value}
